import React from 'react'
import Link from 'next/link'
import { inject, observer } from 'mobx-react'

@inject('CompanyStore')
@observer
export class Company extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            name: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.saveCompany = this.saveCompany.bind(this);

    }

    componentDidMount() {

    }

    componentWillUnmount() {

    }

    saveCompany = (e) => {
        e.preventDefault();

        console.log("called", this.state.name, this.props.CompanyStore.addCompany("Hi"));
        //this.props.CompanyStore.addCompany(props.initialCompanyState)
        this.state.name = "";
    }


    handleChange(e) {
        this.setState({[e.target.name]: e.target.value});
    }


    render() {

        const {CompanyStore} = this.props;

        return (
            <div>
                <h1>Company: {CompanyStore.companyCount}</h1>

                <form onSubmit={e => this.saveCompany(e)}>
                    <input type="text" name="name" onChange={this.handleChange} />
                    <button>Add Company</button>
                </form>

            </div>
        )
    }
}
