import {observable, action, computed} from 'mobx';
import { useStaticRendering } from 'mobx-react'


const isServer = typeof window === 'undefined'
// eslint-disable-next-line react-hooks/rules-of-hooks
useStaticRendering(isServer)

class CompanyStore{
    @observable companyInfo = [];

    @action addCompany = (company) => {
        this.companyInfo.push(company);
    }

    @computed get companyCount(){
        return this.companyInfo.size;
    }
}

const Company = new CompanyStore();
export default Company;

export async function fetchInitialCompanyState() {
    // You can do anything to fetch initial store state
    return {}
}
